mapper.select:
select * from t_user
and loginacct like concat('%', #{queryText}, '%')
order by createtime desc limit #{start}, #{size}

mapper.insert:
insert into t_user (
    loginacct, username, userpswd, email, createtime
) values (
    #{loginacct}, #{username}, #{userpswd}, #{email}, #{createtime}
)

mapper.update:
update t_user 
set loginacct = #{loginacct}, username = #{username}, email = #{email}
where id = #{id}
